sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function () {
  var TBL = 'x_snc_mock_skillup_skill_do_colaborador';

  data.items = [];
  data.canSeeAll = false;

  var isAdmin   = gs.hasRole('admin') || gs.hasRole('x_snc_mock_skillup.admin');
  var isGestor  = gs.hasRole('x_snc_mock_skillup.gestor');
  var isColab   = gs.hasRole('x_snc_mock_skillup.colaborador');

  if (isAdmin || isGestor) {
    data.canSeeAll = true;
  }

  var gr = new GlideRecord(TBL);

  if (!data.canSeeAll) {
    var myEmail = gs.getUser().getEmail();
    if (!myEmail) {
      data.error = 'Usuário sem e-mail cadastrado.';
      return;
    }
    gr.addQuery('colaborador.email', myEmail);
  }
  if (gr.isValidField('ativo')) {
    gr.addQuery('ativo', true);
  }

  gr.orderBy('colaborador.nome');
  gr.orderBy('skill.nome');
  gr.setLimit(500);
  gr.query();

  while (gr.next()) {
    data.items.push({
      colaborador : gr.colaborador.getDisplayValue(),
      skill       : gr.skill.getDisplayValue(),
      nivel       : gr.nivel.getDisplayValue() || (gr.nivel + ''),
      data_inicio : gr.data_inicio ? gr.data_inicio.getDisplayValue() : '',
      data_fim    : gr.data_fim ? gr.data_fim.getDisplayValue() : ''
    });
  }
})();
