sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
};

sp_widget.script:
(function () {
  var TBL = 'x_snc_mock_skillup_skill_do_colaborador';

  data.items = [];

  var gr = new GlideRecord(TBL);
  // lista somente do usuário atual (o dot-walk exige que Colaborador tenha o campo "usuario" -> sys_user)
  gr.addQuery('colaborador.usuario', gs.getUserID());

  // se existir o campo "ativo", filtra por true (ignora se não existir)
  if (gr.isValidField('ativo')) gr.addQuery('ativo', true);

  gr.orderBy('skill.nome'); // ordena pelo nome da skill (display)
  gr.query();

  while (gr.next()) {
    data.items.push({
      skill: gr.skill.getDisplayValue(),
      nivel: gr.nivel.getDisplayValue() || '' + gr.nivel,
      atualizado: gr.sys_updated_on.getDisplayValue()
    });
  }
})();
