sys_script.script:
(function executeRule(current, previous /*null on insert*/) {
  // Não sobrescreve se já houver assignment_group
  if (!gs.nil(current.assignment_group)) return;

  // Normaliza prioridade para detectar P1
  function isCritical(v) {
    if (!v) return false;
    var s = ('' + v).toLowerCase().trim();
    return s.indexOf('1') === 0 || s.indexOf('critical') > -1 || s.indexOf('crítica') > -1 || s.indexOf('critica') > -1;
  }

  if (!isCritical(current.priority)) return;

  // Procura o grupo pelo nome (ajuste se usar outro)
  var groupName = 'BUG - P1 (Critical)';

  var grp = new GlideRecord('sys_user_group');
  grp.addQuery('name', groupName);
  grp.setLimit(1);
  grp.query();

  if (grp.next()) {
    current.assignment_group = grp.getUniqueValue();
    current.work_notes = 'Auto-routed to ' + groupName + ' por BR (P1 detectado).';
  } else {
    gs.warn('BR Bug P1: Grupo "' + groupName + '" não encontrado. Crie o grupo ou ajuste o BR.');
  }
})(current, previous);
