sys_security_acl.script:
function getMyCollaboratorSysId_() {
  var meEmail = gs.getUser().getEmail();
  if (!meEmail) return '';

  var gr = new GlideRecord('x_snc_mock_skillup_colaborador');
  gr.addQuery('email', meEmail);
  gr.setLimit(1);
  gr.query();
  if (gr.next()) {
    return gr.getUniqueValue();
  }

  return '';
}

function isNew_() {
  try {
    return !current || current.isNewRecord() || gs.nil(current.getUniqueValue());
  } catch (e) {
    return true;
  }
}

answer = (gs.hasRole('admin') ||
          gs.hasRole('x_snc_mock_skillup.admin') ||
          gs.hasRole('x_snc_mock_skillup.gestor'));

if (!answer && gs.hasRole('x_snc_mock_skillup.colaborador')) {
  if (isNew_()) {
    answer = true;
  } else {
    var myColl = getMyCollaboratorSysId_(); // sua função utilitária
    answer = (myColl && current.getValue('colaborador') == myColl);
  }
}
