sys_security_acl.script:
function isNew_() { return gs.nil(current.sys_id); }

answer = (gs.hasRole('admin') ||
          gs.hasRole('x_snc_mock_skillup.admin') ||
          gs.hasRole('x_snc_mock_skillup.gestor'));

if (!answer && gs.hasRole('x_snc_mock_skillup.colaborador')) {
  if (isNew_()) {
    answer = true;
  } else {
    var myColl = getMyCollaboratorSysId_();
    answer = (myColl && current.getValue('colaborador') == myColl);
  }
}
