sys_security_acl.script:
function iAmThisCollaborator_() {
  var meId = gs.getUserID();
  var meEmail = (gs.getUser().getEmail() || '').trim().toLowerCase();
  var ok = false;

  if (current.isValidField('usuario'))
    ok = ok || (current.getValue('usuario') == meId);

  if (meEmail && current.isValidField('email')) {
    var recEmail = (current.getValue('email') || '').trim().toLowerCase();
    ok = ok || (recEmail == meEmail);
  }
  return ok;
}

answer = (gs.hasRole('admin') ||
          gs.hasRole('x_snc_mock_skillup.admin') ||
          gs.hasRole('x_snc_mock_skillup.gestor'));

if (!answer && gs.hasRole('x_snc_mock_skillup.colaborador')) {
  answer = iAmThisCollaborator_();
}
